import React, {useState} from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import './ContactBody.css'
// import classes from '*.module.css';

const useStyles = makeStyles((theme) => ({
    root: {
      '& > *': {
        margin: theme.spacing(1),
        width: '90vw'
      },
    },
    
  }));

  
    function Body() {

    const classes = useStyles();

    const [ name, setName ] = useState('');
    const [ email, setEmail ] = useState('');
    const [ phone, setPhone ] = useState('');
    const [ message, setMessage ] = useState('');


    function handleSubmit(e) {
        alert('Thank you for your message. We will get back to you as soon as we can.')
        e.preventDefault();
        setName('');
        setEmail('');
        setPhone('');
        setMessage('');
   }

    return (
        <div className='Contact__Body'>
            <h1 className='Contact-Title'>Contact Us</h1>
            <p className> Ready to take it to the next level? Let’s talk about your project or idea and find out how 
  we can help your business grow. If you are looking for unique digital experiences that’s 
  relatable to your users, drop us a line.</p>
            
            <form className={classes.root} onSubmit={handleSubmit}>
                <div>
                <TextField 
                id="standard-basic" 
                label="Name" 
                style={{ width: '80vw'}}
                inputProps={{ style: {color: 'white' }}}
                value={name}
                onInput={ e=>setName(e.target.value)}
                />
                </div>

                <div>
                <TextField 
                id="standard-basic" 
                label="Email Address"
                style={{ width: '80vw'}}
                inputProps={{ style: {color: 'white' }}}
                value={email}
                onInput={ e=>setEmail(e.target.value)}
                />
                </div>

               <div>
               <TextField 
                id="standard-basic" 
                label="Phone"
                style={{ width: '80vw'}} 
                inputProps={{ style: {color: 'white' }}}
                value={phone}
                onInput={ e=>setPhone(e.target.value)}

                />
                <div>

               </div>
                 <TextField 
                 id="outlined-multiline-static" 
                 label="Your Message"
                 multiline
                 rows={8}
                 style={{ width: '80vw'}}
                 inputProps={{ style: {color: 'white' }}}
                 value={message}
                 onInput={ e=>setMessage(e.target.value)}
                  />
                </div>

                <Button variant="contained" type="submit" className='Button'
                style={{ width: '12em', height: '4em', marginTop: '3em', backgroundColor: 'white'}}
                >Submit</Button>

            </form>
        </div>
    )
}

export default Body
